// import {Form, FormGroup, Button, Label, Input } from 'reactstrap'
import axios from 'axios';

function Create() {
    const[firstName, setFirstName] = useState('');
    const[lastName, setLastName] = useState('');

    console.log(firstName);
    console.log(lastName);

    // const sendDataToAPI = () => {
    //     axios.post(`https://627fa751b1cc1b12625886d2.mockapi.io/crud` , {
    //         firstName,
    //         lastName
    //     })
    // }
  return (
    <section>
        <div className='contentBox'>
            <div className="formBox">
                <h2> React CRUD Operations </h2>
                    <form>
                        <div className="inputBox">
                            <span> First Name </span>
                            <input type="text" name="fname" onChange= {(e) => setFirstName(e.target.value)}  placeholder='First Name' />
                        </div>
                        <div className="inputBox">
                            <span> Last Name </span>
                            <input type="text" name="lname" onChange= {(e) => setLastName(e.target.value)}  placeholder='Last Name'/>
                        </div>
                        {/* <div className="remember">
                            <label><input type="checkbox" name="" id="" /> Remember me</label>
                        </div> */}
                        <div className="inputBox">
                            <input  value="Submit" onClick={console.log(firstName)} name="" id="" />
                        </div>
                    </form>
            </div>
        </div>
    </section>
  )
}

export default Create
