import React, {useState, useEffect}  from 'react'
// import {Form, FormGroup, Button, Label, Input } from 'reactstrap'
import axios from 'axios';

function Create() {
    const[firstName, setFirstName] = useState('');
    const[lastName, setLastName] = useState('');

    const sendDataToAPI = () => {
        axios.post(`https://627fa751b1cc1b12625886d2.mockapi.io/crud` , {
            firstName,
            lastName
        })
        console.log(firstName);
        console.log(lastName);
    }
    useEffect(() => {
        sendDataToAPI();
    }, [] )
    
  return (
    <section>
        <div className='contentBox'>
            <div className="formBox">
                <h2> React CRUD Operations </h2>
                    <form>
                        <div className="inputBox">
                            <span> First Name </span>
                            <input  name="fname" onChange= {(e) => setFirstName(e.target.value)}  placeholder='First Name' />
                        </div>
                        <div className="inputBox">
                            <span> Last Name </span>
                            <input name="lname" onChange= {(e) => setLastName(e.target.value)}  placeholder='Last Name'/>
                        </div>
                        {/* <div className="remember">
                            <label><input type="checkbox" name="" id="" /> Remember me</label>
                        </div> */}
                        <div className="inputBox">
                            <input type="submit" value="Submit" onClick={sendDataToAPI} name="" id="" />
                            {/* <button type="submit" onClick ={sendDataToAPI} > Submit </button> */}
                        </div>
                    </form>
            </div>
        </div>
    </section>
  )
}

export default Create
