import React, {useState, useEffect}  from 'react'
// import {Form, FormGroup, Button, Label, Input } from 'reactstrap'
import axios from 'axios';

function Create(props) {
    const [firstName,setFirstName]=React.useState('');
    const [lastName,setLastName]=React.useState('');

    console.log(firstName);
    console.log(lastName);

        function SendData(){
            console.log(firstName);
            console.log(lastName);
    
            axios
            .post('https://627fa751b1cc1b12625886d2.mockapi.io/crud',{
                firstName,
                lastName
            })
            .then(function(response){
                console.log("Response from the axios recieved",response.data)
            })
            .catch(function(error){
                console.log(error)
            })
        }
    // useEffect(() => {
        
    //  }, [] )
    
  return (
    <section>
        <div className='contentBox'>
            <div className="formBox">
                <h2> React CRUD Operations </h2>
                    <form>
                        <div className="inputBox">
                            <span> First Name </span>
                            <input  name="fname" value={firstName} onChangeText={(text)=>setFirstName(text)}  placeholder='First Name' />
                        </div>
                        <div className="inputBox">
                            <span> Last Name </span>
                            <input name="lname" value={lastName} onChangeText={(text)=>setLastName(text)}  placeholder='Last Name'/>
                        </div>
                        {/* <div className="remember">
                            <label><input type="checkbox" name="" id="" /> Remember me</label>
                        </div> */}
                        <div className="inputBox">
                            {/* <input type="submit" value="Submit"  onClick ={sendDataToAPI} /> */}
                            <button type="submit" onClick={SendData}> Submit </button>
                        </div>
                    </form>
            </div>
        </div>
    </section>
  )
}

export default Create;
