import React, {useEffect,useState} from 'react';
// import {Table } from 'reactstrap'
import axios from 'axios';
import { Link } from 'react-router-dom';

function Read() {

    const [apiData, setApiData] = useState([]);

    useEffect(() => {
        axios.get(`https://627fa751b1cc1b12625886d2.mockapi.io/crud`)
        .then((getData) => {
            setApiData(getData.data);
        })
    }, []);

    const setID = (id) => {
        console.log(id);
    }

  return (
    <div className='tableBox'>
        <table>
            <thead>
                <tr>
                    <th>Number</th>
                    <th>F Name </th>
                    <th>L Name </th>
                    <th>Update</th>
                    <th>Delete</th>
                </tr>
            </thead>
            <tbody>
                {apiData.map((data) => {
                    return (
                        <tr>
                            <th scope="row">{data.id}</th>
                            <td>{data.firstName}</td>
                            <td>{data.lastName}</td>
                            <td>
                                <Link to ='/update'>
                                    <button type="button" onClick={() => setID(data.id)} className='update'>Update</button>
                                </Link>
                            </td>
                            <td>
                                <Link to ='/delete'>
                                    <button type="button" className='delete'>Delete</button>
                                </Link>
                            </td>
                        </tr>
                    )
                })}
            </tbody>
        </table>
    </div>
    
  )
}

export default Read
