import React, {useEffect,useState} from 'react';
// import {Table } from 'reactstrap'
import axios from 'axios';
import { Link } from 'react-router-dom';

function Read() {

    const [apiData, setApiData] = useState([]);
    useEffect(() => {
        axios.get(`https://627fa751b1cc1b12625886d2.mockapi.io/crud`)
        .then((getData) => {
            setApiData(getData.data);
        })
    }, []);

    const setData = (id, firstName, lastName) => {
        console.log(id);
        localStorage.setItem('ID', id);
        localStorage.getItem('firstName');
        localStorage.getItem('lastName');
    }

    const getData = () => {
        axios.get(`https://627fa751b1cc1b12625886d2.mockapi.io/crud`)
        .then((getData) => {
            setApiData(getData.data);
        })
    }

    const onDelete= () => {
        axios.delete(`https://627fa751b1cc1b12625886d2.mockapi.io/crud`)
        .then((getData) => {
            setApiData(getData.data);
        })
    }

  return (
    <div className='tableBox'>
        <table>
            <thead className='thead-class'>
                <tr>
                    <th>Number</th>
                    <th>F Name </th>
                    <th>L Name </th>
                    <th>Update</th>
                    <th>Delete</th>
                </tr>
            </thead>
            <tbody>
                {apiData.map((data) => {
                    return (
                        <tr>
                            <th scope="row">{data.id}</th>
                            <td>{data.firstName}</td>
                            <td>{data.lastName}</td>
                            <td>
                                <Link to ='/update'>  
                                    {/* onClick={() => setID(data.id)} */}
                                    <button type="button" value='firstName'  className='update'>Update</button>
                                </Link>
                            </td>
                            <td>
                                <Link to ='/delete'>
                                    <button type="button" value='lastName' className='delete'>Delete</button>
                                </Link>
                            </td>
                        </tr>
                    )
                })}
            </tbody>
        </table>
    </div>
    
  )
}

export default Read
